mapper.insert:
insert into user(id, password, name, email, phone, address, allergy, grant) 
			values(#{id:VARCHAR}
				  ,#{password:VARCHAR}
				  ,#{name:VARCHAR}
				  ,#{email:VARCHAR}
				  ,#{phone:VARCHAR}
				  ,#{address:VARCHAR}
				  ,#{allergy:VARCHAR}
				  ,#{grant:VARCHAR})

mapper.update:
update user set password= #{password:VARCHAR}
				, name=#{name:VARCHAR}
				, email=#{email:VARCHAR}
				, phone=#{phone:VARCHAR}
				, address=#{address:VARCHAR}
				, allergy=#{allergy:VARCHAR}
				, grant=#{grant:VARCHAR}
where id = #{id:VARCHAR}

